export function QuantityNumber({
    name,
    label,
    required,
    value = 0,
    onChange,
    error
}: {
    name?: string;
    label?: string;
    required?: boolean;
    value?: number;
    onChange?: (e: ChangeEvent<HTMLInputElement | any>) => void;
    error?: string;
}) {
    const [qty, setQty] = useState<number>(value);

    // Keep local state in sync with parent value
    useEffect(() => {
        setQty(value);
    }, [value]);

    const handleChange = (newValue: number) => {
        if (newValue >= 0) {
            setQty(newValue);

            // trigger parent onChange like a real input event
            if (onChange) {
                const syntheticEvent = {
                    target: { name, value: Number(newValue) },
                } as unknown as ChangeEvent<HTMLInputElement>;

                onChange(syntheticEvent);
            }
        }
    };

    return (
        <>
            {label && <InputFieldLabel label={label} required={required} />}
            <div className="d-flex Quantity-box">
                <span
                    className="Quantity-box-minues w-100 d-flex align-items-center justify-content-center"
                    onClick={() => handleChange(qty - 1)}
                >
                    <svg xmlns="http://www.w3.org/2000/svg" width="17" height="2" viewBox="0 0 17 2" fill="none">
                        <path d="M16.4668 1C16.4668 1.24864 16.368 1.4871 16.1922 1.66291C16.0164 1.83873 15.7779 1.9375 15.5293 1.9375H1.7793C1.53066 1.9375 1.2922 1.83873 1.11638 1.66291C0.940569 1.4871 0.841797 1.24864 0.841797 1C0.841797 0.75136 0.940569 0.512903 1.11638 0.337087C1.2922 0.161272 1.53066 0.0625 1.7793 0.0625H15.5293C15.7779 0.0625 16.0164 0.161272 16.1922 0.337087C16.368 0.512903 16.4668 0.75136 16.4668 1Z" fill="#2B4360" />
                    </svg>
                </span>
                <FormControl
                    type="number"
                    name={name}
                    value={qty}
                    readOnly
                    className="text-center w-100 rounded-0 Quantity-box-show"
                />
                <span
                    className="Quantity-box-plus w-100 d-flex align-items-center justify-content-center"
                    onClick={() => handleChange(qty + 1)}
                >
                    <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 20 20" fill="none">
                        <path d="M17.8125 10C17.8125 10.2486 17.7137 10.4871 17.5379 10.6629C17.3621 10.8387 17.1236 10.9375 16.875 10.9375H10.9375V16.875C10.9375 17.1236 10.8387 17.3621 10.6629 17.5379C10.4871 17.7137 10.2486 17.8125 10 17.8125C9.75136 17.8125 9.5129 17.7137 9.33709 17.5379C9.16127 17.3621 9.0625 17.1236 9.0625 16.875V10.9375H3.125C2.87636 10.9375 2.6379 10.8387 2.46209 10.6629C2.28627 10.4871 2.1875 10.2486 2.1875 10C2.1875 9.75136 2.28627 9.5129 2.46209 9.33709C2.6379 9.16127 2.87636 9.0625 3.125 9.0625H9.0625V3.125C9.0625 2.87636 9.16127 2.6379 9.33709 2.46209C9.5129 2.28627 9.75136 2.1875 10 2.1875C10.2486 2.1875 10.4871 2.28627 10.6629 2.46209C10.8387 2.6379 10.9375 2.87636 10.9375 3.125V9.0625H16.875C17.1236 9.0625 17.3621 9.16127 17.5379 9.33709C17.7137 9.5129 17.8125 9.75136 17.8125 10Z" fill="#2B4360" />
                    </svg>
                </span>
            </div>

            {error && <InputFieldError error={error} />}
        </>
    );
}

